// ./src/store/state.js

export default {
    fillTodos (state, todos) {
        state.todos = todos
    },
    addTodo (state, todo) {
        state.todos.unshift(todo)
    },
    updateTodo (state, { id, todo }) {
        state.todos.splice(
            state.todos.findIndex(t => t.id === id),
            1,
            todo
        )
    },
    deleteTodo (state, id) {
        state.todos.splice(
            state.todos.findIndex(t => t.id === id),
            1
        )
    }
}


